    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Article{% endblock %}

{% block body %}
    <div class = \"coconuts-container\">
        <h2>
            {{ article.title }} <a href=\"\"><i class=\"far fa-bookmark\"></i></a>
        </h2>
        <div>
            <a href = \"{{ path('user_show', {'id': article.user.id}) }}\">Par {{ article.user.username }}</a>
        </div>
        <div>
            <a href=\"\"><i class=\"far fa-heart\"></i></a>
            {{article.nbOfLikes}}
            <a href=\"\"><i class=\"fas fa-glasses\"></i></a>
            {{article.nbOfVisits}}
            <a href=\"\"><i class=\"far fa-comment \"></i></a>
            {{article.nbOfComment}}
        </div>
        <br>
        <div>
            <img src=\"{{ vich_uploader_asset(article) }}\" alt=\"{{ article.title }}\"/><br>
            <div>{{ article.description }} </div>
        </div><br>

        <!-- BLOC AUTEUR -->
        {{ render(controller(
            'App\\\\Controller\\\\WriterController::displayPartialWriterResumeBlock',
            { 'idUser': article.user.id }
        )) }}

        <a href=\"{{ path('article_index') }}\">Retour</a>

        {% if app.user is not null %}
            {% if app.user.id == article.user.id %}
                <a href=\"{{ path('article_edit', {'id': article.id}) }}\">Modifier votre article</a>
            {% endif %}
        {% endif %}
    </div>
{% endblock %}
", "article/show.html.twig", "/home/pascal/Rendu/ProjetCocoNuts/ProjetPognon/templates/article/show.html.twig");
